package com.google.ads.googleads.examples.assets;

import com.beust.jcommander.Parameter;
import com.google.ads.googleads.examples.utils.ArgumentNames;
import com.google.ads.googleads.examples.utils.CodeSampleParams;
import com.google.ads.googleads.lib.GoogleAdsClient;
import com.google.ads.googleads.v22.common.Money;
import com.google.ads.googleads.v22.common.PriceAsset;
import com.google.ads.googleads.v22.common.PriceOffering;
import com.google.ads.googleads.v22.enums.AssetFieldTypeEnum.AssetFieldType;
import com.google.ads.googleads.v22.enums.PriceExtensionPriceQualifierEnum.PriceExtensionPriceQualifier;
import com.google.ads.googleads.v22.enums.PriceExtensionPriceUnitEnum.PriceExtensionPriceUnit;
import com.google.ads.googleads.v22.enums.PriceExtensionTypeEnum.PriceExtensionType;
import com.google.ads.googleads.v22.errors.GoogleAdsError;
import com.google.ads.googleads.v22.errors.GoogleAdsException;
import com.google.ads.googleads.v22.resources.Asset;
import com.google.ads.googleads.v22.resources.CustomerAsset;
import com.google.ads.googleads.v22.services.AssetOperation;
import com.google.ads.googleads.v22.services.AssetServiceClient;
